	openBracket: /\x5B/g,
	escapedOpenBracket: /\x5C\x5B/g,
	escapedCloseBracket: /\x5C\x5D/g,
	percentPercent: /%%/g,
	atPercent: /@%/g,
	percentAt: /%@/g,
	singleQuote: /\x27/g,
